import React, { useState, useCallback, useEffect, useRef } from 'react';
import './GamePage.css';

const createEmptyGrid = (rows, cols) => {
  return Array.from({ length: rows }).map(() => Array.from({ length: cols }).fill({ alive: 0, generation: 0 }));
};

const createRandomGrid = (rows, cols) => {
  const grid = createEmptyGrid(rows, cols);
  for (let i = 0; i < rows; i++) {
    for (let j = 0; j < cols; j++) {
      grid[i][j] = { alive: Math.random() < 0.05 ? 1 : 0, generation: 0 };
    }
  }
  return grid;
};

const GamePage = () => {
  const defaultSize = 20;
  const [gridSize, setGridSize] = useState({ rows: defaultSize, cols: defaultSize });
  const [inputValues, setInputValues] = useState({ rows: '', cols: '' });
  const [grid, setGrid] = useState(() => createRandomGrid(defaultSize, defaultSize));
  const [inputError, setInputError] = useState('');
  const [isPlaying, setIsPlaying] = useState(false);
  const playRef = useRef(isPlaying);
  playRef.current = isPlaying;

  const handleSizeChange = (e, dimension) => {
    setInputValues({ ...inputValues, [dimension]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const rows = parseInt(inputValues.rows, 10) || defaultSize;
    const cols = parseInt(inputValues.cols, 10) || defaultSize;
    if (rows >= 3 && rows <= 40 && cols >= 3 && cols <= 40) {
      setGridSize({ rows, cols });
      setGrid(createRandomGrid(rows, cols));
      setInputError('');
    } else {
      setInputError('Please enter numbers between 3 and 40 for both rows and cols.');
    }
  };

  const toggleCellState = (row, col) => {
    const newGrid = JSON.parse(JSON.stringify(grid));
    newGrid[row][col].alive = grid[row][col].alive ? 0 : 1;
    setGrid(newGrid);
  };

  const nextGeneration = useCallback(() => {
    if (!playRef.current) return;
    setGrid((currentGrid) => {
      return currentGrid.map((row, i) =>
        row.map((cell, j) => {
          let liveNeighbors = 0;
          const neighbors = [
            [i - 1, j - 1], [i - 1, j], [i - 1, j + 1],
            [i, j - 1],               [i, j + 1],
            [i + 1, j - 1], [i + 1, j], [i + 1, j + 1],
          ];

          neighbors.forEach(([x, y]) => {
            if (x >= 0 && x < gridSize.rows && y >= 0 && y < gridSize.cols) {
              liveNeighbors += currentGrid[x][y]?.alive || 0;
            }
          });

          if (cell.alive) {
            if (liveNeighbors < 2 || liveNeighbors > 3) {
              return { alive: 0, generation: 0 };
            }
            return { ...cell, generation: cell.generation + 1 };
          } else {
            if (liveNeighbors === 3) {
              return { alive: 1, generation: 1 };
            }
            return cell;
          }
        })
      );
    });
  }, [gridSize]);

  useEffect(() => {
    if (isPlaying) {
      const interval = setInterval(() => {
        nextGeneration();
      }, 100);
      return () => clearInterval(interval);
    }
  }, [isPlaying, nextGeneration]);

  const resetGrid = () => {
    setGrid(createRandomGrid(gridSize.rows, gridSize.cols));
  };

  return (
    <div className="game-page">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Rows (20)"
          value={inputValues.rows}
          onChange={e => handleSizeChange(e, 'rows')}
        />
        <input
          type="text"
          placeholder="Cols (20)"
          value={inputValues.cols}
          onChange={e => handleSizeChange(e, 'cols')}
        />
        <button type="submit">Update Grid Size</button>
      </form>
      {inputError && <div className="error-message">{inputError}</div>}
      <div className="game-board" style={{ gridTemplateColumns: `repeat(${gridSize.cols}, 20px)` }}>
        {grid.map((row, i) =>
          row.map((cell, j) => (
            <div
              key={`${i}-${j}`}
              onClick={() => toggleCellState(i, j)}
              className={`cell ${cell.alive ? 'alive' : 'dead'}`}
              style={{ backgroundColor: cell.alive ? `hsl(${Math.min(120, cell.generation * 12)}, 100%, 50%)` : undefined }}
            />
          ))
        )}
      </div>
      <button onClick={() => setIsPlaying(!isPlaying)}>{isPlaying ? "Stop" : "Autoplay"}</button>
      <button onClick={nextGeneration}>Next Generation</button>
      <button onClick={resetGrid}>Reset Grid</button>
    </div>
  );
};

export default GamePage;
