import React, { useState, useEffect, useCallback } from 'react';
import './GamePage.css';

// Helper function to create an empty grid
const createEmptyGrid = (rows, cols) => {
    return Array.from({ length: rows }).map(() => Array.from({ length: cols }).fill(0));
};

// Helper function to create a grid with random live cells
const createRandomGrid = (rows, cols) => {
    const grid = createEmptyGrid(rows, cols);
    for (let i = 0; i < rows; i++) {
        for (let j = 0; j < cols; j++) {
            grid[i][j] = Math.random() < 0.05 ? 1 : 0; // 5% chance of being alive
        }
    }
    return grid;
};

const GamePage = () => {
    const [grid, setGrid] = useState(() => createRandomGrid(20, 20));
    const [rows, setRows] = useState(20);
    const [cols, setCols] = useState(20);

    // Toggle cell state
    const toggleCellState = (row, col) => {
        const newGrid = JSON.parse(JSON.stringify(grid));
        newGrid[row][col] = grid[row][col] ? 0 : 1;
        setGrid(newGrid);
    };

    // Calculate the next generation of cells
    const nextGeneration = useCallback(() => {
        setGrid((g) => {
            return g.map((row, i) =>
                row.map((cell, j) => {
                    const neighbors = [
                        [i - 1, j - 1], [i - 1, j], [i - 1, j + 1],
                        [i, j - 1], /* current cell */ [i, j + 1],
                        [i + 1, j - 1], [i + 1, j], [i + 1, j + 1],
                    ];
                    const liveNeighbors = neighbors.reduce((acc, [x, y]) => {
                        if (x >= 0 && x < rows && y >= 0 && y < cols) {
                            acc += g[x][y];
                        }
                        return acc;
                    }, 0);

                    if (cell === 1 && (liveNeighbors < 2 || liveNeighbors > 3)) return 0;
                    if (cell === 0 && liveNeighbors === 3) return 1;
                    return cell;
                })
            );
        });
    }, [rows, cols]);

    // Reset grid to random state
    const resetGrid = () => {
        setGrid(createRandomGrid(rows, cols));
    };

    return (
        <div className="game-page">
            <div className="game-board">
                {grid.map((row, i) =>
                    row.map((cell, j) => (
                        <div
                            key={`${i}-${j}`}
                            onClick={() => toggleCellState(i, j)}
                            className={`cell ${cell ? 'alive' : 'dead'}`}
                        />
                    ))
                )}
            </div>
            <button onClick={nextGeneration}>Next Generation</button>
            <button onClick={resetGrid}>Reset Grid</button>
        </div>
    );
};

export default GamePage;
